drop table BOWLS_CLUB.t_bowl_player;
drop table BOWLS_CLUB.t_api_permission;
drop table BOWLS_CLUB.t_bowl_competition;
drop table BOWLS_CLUB.t_bowl_game_player_performance;
drop table BOWLS_CLUB.t_user_info;
drop table BOWLS_CLUB.t_bowl_team;

create table t_bowl_competition
(
    id               bigint auto_increment
        primary key,
    team_id          bigint       default -1                                 not null comment 'team id ',
    competition_name varchar(128) default 'New Competition'                  not null comment 'Competition Name',
    competition_day  varchar(256) default 'Day of competition for each week' null
)
    comment 'record for a single bowl match';


create table t_bowl_game_player_performance
(
    id                int auto_increment
        primary key,
    player_id         bigint                  not null,
    competition_id    bigint                  not null,
    performance_score int          default -1 not null,
    season            varchar(256)            not null comment 'The Season of the competition(seperate by year)',
    match_time        datetime                not null,
    position          varchar(256)            not null comment 'Postion for the player of this game',
    competition_name  varchar(256) default '' not null
);

create table t_bowl_player
(
    id                          bigint auto_increment
        primary key,
    player_name                 varchar(256)  default 'Player' not null,
    player_email                varchar(256)  default ''       not null,
    player_gender               varchar(256)  default ''       not null,
    player_phone                varchar(256)  default ''       not null,
    player_availability         varchar(256)  default ''       not null comment 'plain text of available weekdays',
    player_pos_preference       varchar(256)  default ''       not null comment 'plain text of preferred position',
    player_prefer_teammates     varchar(256)  default ''       not null comment 'plain text of the names the player like to play with',
    player_not_prefer_teammates varchar(256)  default ''       not null comment 'plain text of the names the player do not like to play with',
    photo_url                   varchar(512)  default ''       null,
    recent_performance          double        default 0        not null comment 'Calculated by the recent five games'' performance of the player',
    notes                       varchar(1024) default 'empty'  not null
)
    comment 'record for player information';


create table t_bowl_team
