    lead_bowler_name1   varchar(256) default 'Lead bowler'   not null comment 'name of the skip player',
    skip_bowler_id2     bigint       default -1              not null comment 'player id of the skip bowler',
    skip_bowler_name2   varchar(256) default 'Skip bowler'   not null comment 'name of the skip player',
    third_bowler_id2    bigint       default -1              not null comment 'player id of the third_bowler',
    third_bowler_name2  varchar(256) default 'Third bowler'  not null comment 'name of the third_bowler',
    second_bowler_id2   bigint       default -1              not null comment 'player id of the second_bowler',
    second_bowler_name2 varchar(256) default 'Second bowler' not null comment 'name of the second_bowler',
    lead_bowler_id2     bigint       default -1              not null comment 'player id of the lead bowler',
    lead_bowler_name2   varchar(256) default 'Lead bowler'   not null comment 'name of the skip player',
    skip_bowler_id3     bigint       default -1              not null comment 'player id of the skip bowler',
    skip_bowler_name3   varchar(256) default 'Skip bowler'   not null comment 'name of the skip player',
    third_bowler_id3    bigint       default -1              not null comment 'player id of the third_bowler',
    third_bowler_name3  varchar(256) default 'Third bowler'  not null comment 'name of the third_bowler',
    second_bowler_id3   bigint       default -1              not null comment 'player id of the second_bowler',
    second_bowler_name3 varchar(256) default 'Second bowler' not null comment 'name of the second_bowler',
    lead_bowler_id3     bigint       default -1              not null comment 'player id of the lead bowler',
    lead_bowler_name3   varchar(256) default 'Lead bowler'   not null comment 'name of the skip player',
    skip_bowler_id4     bigint       default -1              not null comment 'player id of the skip bowler',
    skip_bowler_name4   varchar(256) default 'Skip bowler'   not null comment 'name of the skip player',
    third_bowler_id4    bigint       default -1              not null comment 'player id of the third_bowler',
    third_bowler_name4  varchar(256) default 'Third bowler'  not null comment 'name of the third_bowler',
    second_bowler_id4   bigint       default -1              not null comment 'player id of the second_bowler',
    second_bowler_name4 varchar(256) default 'Second bowler' not null comment 'name of the second_bowler',
    lead_bowler_id4     bigint       default -1              not null comment 'player id of the lead bowler',
    lead_bowler_name4   varchar(256) default 'Lead bowler'   not null comment 'name of the skip player'
