    comment 'record for the bowl teams';
    
    create table t_api_permission
(
    id   int auto_increment,
    uri  varchar(100) default '\\'       null,
    role varchar(50)  default 'selector' not null,
    constraint t_api_permission_id_uindex
        unique (id)
)
    comment 'Permission control';

alter table t_api_permission
    add primary key (id);


create table t_user_info
(
    id                int auto_increment
        primary key,
    user_name         varchar(50)  default 'Nick Name'        not null,
    real_name         varchar(50)  default 'Real Name'        not null,
    email             varchar(100) default 'name@example.com' not null,
    password          varchar(50)  default '123456'           not null,
    role              varchar(50)  default 'selector'         not null,
    token             varchar(100) default 'token'            not null,
    token_create_date datetime                                null
)
    comment 'Selector and Admin Table';

